                </View>
              ) : (
                <View>
                  <Text
                    style={{ textAlign: "center", fontSize: 20, marginTop: 10 }}
                  >
                    Estimated Budjet: {surprise.tourCost}
                  </Text>
                  <TouchableOpacity>
                    <View style={{ alignItems: "center", margin: 10 }}>
                      <Text
                        style={{
                          textAlign: "center",
                          padding: 13,
                          marginBottom: 20,
                          fontSize: 20,
                          borderWidth: 1,
                          borderColor: "black",
                          borderRadius: 10,
                          backgroundColor: "#12CBC4",
                        }}
                      >
                        Pay Now
                      </Text>
                    </View>
                  </TouchableOpacity>
                </View>
              )}
            </Surface>
          )}
        </View>
      ) : null}
    </ScrollView>
  );
};

export default RequestInner;

