import React, { useState, useEffect, useContext } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  ScrollView,
  FlatList,
  Platform,
  Dimensions,
  StatusBar,
  TextInput,
  TouchableOpacity,
  Button,
  ActivityIndicator,
} from "react-native";
import { AuthContext } from "../../context/AuthContext";
import firebase from "firebase";
import touron from "../../api/touron";
import { LinearGradient } from "expo-linear-gradient";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

import { AntDesign, Feather, MaterialIcons } from "@expo/vector-icons";

import { Surface } from "react-native-paper";

const TourHomeScreen = ({ navigation, route }) => {
  const { isLoggedIn, user, tours } = useContext(AuthContext);
  const [tour, setTour] = useState(tours);
  const [selected, setSelected] = useState([]);
  const [step, setStep] = useState(0);
  const [tourLoaded, settourLoaded] = useState(false);

  const nextStep = () => {
    setIdealType("");
    setTourType("");
    setTourCategory("");
    setStep(step + 1);
  };
  const cancel = () => {
    setIdealType("");
    setTourType("");
    setTourCategory("");
    setStep(step - 1);
    setSelected([]);
  };
  const prevStep = () => {
    setLoader(true);

    setStep(step - 1);
    setTimeout(() => {
      setLoader(false);
    }, 2000);
  };

  const getSavedTours = () => {
    let sT = [];
    let sTNames = [];
    if (isLoggedIn) {
      firebase
        .database()
        .ref(`saved-tours/${user.uid}`)
        .on("value", (data) => {
          if (data) {
            data.forEach((c) => {
              sT.push(c.val());
              sTNames.push(c.val().tourName);
            });
          }
        });

      return [sT, sTNames];
    } else {
      return [sT, sTNames];
    }
  };

  const [sT, sTNames] = getSavedTours();
  const [error, setErrorMessage] = useState();
  const [loader, setLoader] = useState(true);
  const [mainLoaded, setMainLoaded] = useState(true);
  const [savedTours, setSavedTours] = useState(sTNames);
  const [savedToursDetails, setSavedToursDetails] = useState(sT);
  const [tourName, setTourName] = useState("");

  const [idealType, setIdealType] = useState("");
  const [tourType, setTourType] = useState("");
  const [tourCategory, setTourCategory] = useState("");
  const [page, setPage] = useState(1);
  const [pageSize, setpageSize] = useState(10);

  let indexValue = 20;
  const showLoader = () => {
    setTimeout(() => {
      setLoader(false);
      setMainLoaded(false);
    }, 2000);
  };

  const search = () => {
    if (tour.length > 0) {
      const d = tour.filter((c) => {
        return c.tourName
          .trim()
          .toUpperCase()
          .includes(tourName.trim().toUpperCase().trim());
      });
      return d;
    }
  };

  // console.log(search(), "search");
  useEffect(() => {
    if (isLoggedIn) {
      if (savedToursDetails.length > 0) {
        firebase
          .database()
          .ref(`saved-tours/${user.uid}`)
          .set(savedToursDetails)
          .then((data) => console.log(data))
          .catch((err) => console.log(err));
      }
    }
  }, [tour, savedToursDetails, indexValue]);

  const getTour = async () => {
    if (route.params.name) {
      const cityname = route.params.name;
      try {
        const tourResponse = await touron.get(`/tour/cityname/${cityname}`);
        if (tourResponse.data.length === 0) {
          setTour([]);
        }
        setTour(tourResponse.data);
      } catch (err) {
        setErrorMessage("Something went wrong");
      }
    } else {
      // try {
      //   if (idealType != "") {
      //     setLoader(true);
      //     const tourResponse = await touron.get(`/tour/idealtype/${idealType}`);
      //     // idealTypeTours.push(tourResponse.data);
      //     setTour(tourResponse.data);
      //     setLoader(false);
      //   }
      //   if (tourType != "") {
      //     setLoader(true);
      //     const tourResponse = await touron.get(`/tour/tourtype/${tourType}`);
      //     // tourTypeTours.push(tourResponse.data);
      //     setTour(tourResponse.data);
      //     setLoader(false);
      //   }
      //   if (tourCategory != "") {
      //     setLoader(true);
      //     const tourResponse = await touron.get(
      //       `/tour/tourcategory/${tourCategory}`
      //     );
      //     // tourCategoryTours.push(tourResponse.data);
      //     setTour(tourResponse.data);
      //     setLoader(false);
      //   }
      // } catch (err) {
      //   setErrorMessage("Something went wrong");
      // }
    }
  };

  const searchTour = async () => {
    setLoader(true);
    setStep(step - 1);

    if (idealType != "") {
      const tourResponse = await touron.get(`/tour/idealtype/${idealType}`);
      // idealTypeTours.push(tourResponse.data);
      setTour(tourResponse.data);
      setLoader(false);
    }

    if (tourType != "") {
      const tourResponse = await touron.get(`/tour/tourtype/${tourType}`);
      // tourTypeTours.push(tourResponse.data);
      setTour(tourResponse.data);
      setLoader(false);
    }
    if (tourCategory != "") {
      const tourResponse = await touron.get(
        `/tour/tourcategory/${tourCategory}`
      );
      // tourCategoryTours.push(tourResponse.data);
      setTour(tourResponse.data);
      setLoader(false);
    }
  };

  useEffect(() => {
    showLoader();
    let mounted = true;
    if (mounted) {
      getTour();
    }
    return () => (mounted = false);
  }, [idealType, tourCategory, tourType]);

  const render = (step) => {
    const idealFor = [
      {
        name: "Family",
      },
      {
        name: "Family and kids",
      },
      { name: "Solo" },
      { name: "Mature Couple" },
      { name: "Young Couple" },
    ];

    const tourType = [
      {
        name: "Full Day Tour",
      },
      {
        name: "Half Day Tour",
      },
      { name: "Night Tour" },
    ];
    const tourCategory = [
      {
        name: "Activities",
      },
      { name: "Hop On & Off" },
      {
        name: "Attraction",
      },
      { name: "Learning" },
    ];
    switch (step) {
      case 0:
        return (
          <View
            style={
              ([styles.container],
              {
                backgroundColor: tour.length === 0 ? "white" : "transparent",
                flex: 1,
              })
            }
          >
            {loader ? (
              <ActivityIndicator
                size="large"
                style={{
                  flex: 1,
                  alignItems: "center",
                  justifyContent: "center",
                }}
              />
            ) : (
              <View>
                <View
                  style={{
                    height: HEIGHT / 8,
                    // backgroundColor: "#ff6b6b",
                    padding: 10,
                    marginTop: Platform.OS === "ios" ? HEIGHT / 12 : 40,
                    width: WIDTH,
                    // alignItems: "center",
                    justifyContent: "center",
                  }}
                >
                  <View
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      justifyContent: "space-between",
                      alignItems: "center",
                    }}
                  >
                    <TouchableOpacity onPress={() => navigation.goBack()}>
                      <Feather name="arrow-left" style={{ fontSize: 30 }} />
                    </TouchableOpacity>

                    <View>
                      <Text style={{ fontSize: 23, fontFamily: "NewYorkl" }}>
                        Tours
                      </Text>
                    </View>
                    <TouchableOpacity onPress={() => nextStep()}>
                      <MaterialIcons
                        name="sort"
                        size={24}
                        color="black"
                        style={{ paddingRight: 10 }}
                      />
                    </TouchableOpacity>
                  </View>
                  <View style={styles.background}>
                    <Feather name="search" style={styles.iconStyle}></Feather>
                    <TextInput
                      style={styles.inputStyle}
                      placeholder="Search"
                      onChangeText={(value) => setTourName(value)}
                      onSubmitEditing={search}
                      autoCapitalize="none"
                      autoCorrect={false}
                      keyboardType="email-address"
                    />
                  </View>
                </View>

                {tour.length === 0 && route.params.name ? (
                  <View
                    style={{ alignItems: "center", justifyContent: "center" }}
                  >
                    <View>
                      <Image
                        style={{ width: WIDTH * 0.9, height: WIDTH * 0.9 }}
                        source={require("../../../assets/oops.jpg")}
                      />
                    </View>
                    <Text style={{ fontFamily: "Avenir", fontSize: 23 }}>
                      No Tours Found for {route.params.name}
                    </Text>
                  </View>
                ) : (
                  <>
                    {mainLoaded ? (
                      <View
                        style={{
                          flex: 1,
                          alignItems: "center",
                          justifyContent: "center",
                        }}
                      >
                        <ActivityIndicator
                          size="large"
                          style={{
                            flex: 1,
                            alignItems: "center",
                            justifyContent: "center",
                          }}
                        ></ActivityIndicator>
                      </View>
                    ) : (
                      <FlatList
                        showsVerticalScrollIndicator={false}
                        keyExtractor={(c) => c._id}
                        data={search()}
                        renderItem={({ item, index }) => {
                          return (
                            <>
                              <View style={styles.imageContainer}>
                                <View style={styles.shadow}>
                                  <TouchableOpacity
                                    onPress={() => {
                                      navigation.navigate("TourInner", {
                                        item: item,
                                      });
                                    }}
                                  >
                                    <Image
                                      style={styles.image}
                                      source={{ uri: item.imageUrl }}
                                    />
                                  </TouchableOpacity>
                                  <View
                                    style={{
                                      flexDirection: "row",
                                      position: "absolute",
                                      width: WIDTH * 0.9,
                                      justifyContent: "space-between",
                                      alignItems: "center",
                                    }}
                                  >
                                    <LinearGradient
                                      colors={["#FFA26E", "#E36D5D"]}
                                      style={{
                                        marginHorizontals: 15,
                                        marginTop: 10,
                                        padding: 2,
                                        borderRadius: 5,
                                        left: 10,
                                      }}
                                    >
                                      <View>
                                        <Text style={styles.cityname}>
                                          {item.cityName}
                                        </Text>
                                      </View>
                                    </LinearGradient>
                                  </View>
                                  <View style={styles.tourDetails}>
                                    <View
                                      style={{
                                        flexDirection: "row",
                                        justifyContent: "space-between",
                                      }}
                                    >
                                      <Text
                                        style={{
                                          color: "#8395A7",
                                          fontSize: 15,
                                          fontFamily: "Andika",
                                        }}
                                      >
                                        {item.tourCategory.join(",")}
                                      </Text>
                                      <View>
                                        {savedTours.includes(item.tourName) ? (
                                          <TouchableOpacity
                                            onPress={() => {
                                              let filterData = savedTours.filter(
                                                (c) => {
                                                  return c != item.tourName;
                                                }
                                              );

                                              setSavedTours(filterData);

                                              const filterDetails = savedToursDetails.filter(
                                                (c) => {
                                                  return (
                                                    c.tourName !== item.tourName
                                                  );
                                                }
                                              );

                                              setSavedToursDetails(
                                                filterDetails
                                              );
                                            }}
                                          >
                                            <AntDesign
                                              style={{ marginRight: 0 }}
                                              name="heart"
                                              size={24}
                                              color="#FF4500"
                                            />
                                          </TouchableOpacity>
                                        ) : (
                                          <TouchableOpacity
                                            onPress={() => {
                                              if (isLoggedIn) {
                                                setSavedTours([
                                                  ...savedTours,
                                                  item.tourName,
                                                ]);
                                                setSavedToursDetails([
                                                  ...savedToursDetails,
                                                  item,
                                                ]);
                                              } else {
                                                navigation.navigate(
                                                  "SignUpScreen"
                                                );
                                              }
                                            }}
                                          >
                                            <Image
                                              style={{
                                                height: 35,

                                                width: 35,
                                              }}
                                              source={require("../../../assets/heart.png")}
                                            />
                                          </TouchableOpacity>
                                        )}
                                      </View>
                                    </View>

                                    <Text
                                      style={{
                                        fontSize: 18,
                                        // fontWeight: "bold",
                                        fontFamily: "NewYorkl",
                                        marginTop: 2,
                                      }}
                                    >
                                      {item.tourName}
                                    </Text>
                                    <Text
                                      style={{
                                        color: "#8395A7",
                                        fontSize: 15,
                                        marginVertical: 5,
                                        fontFamily: "Andika",
                                      }}
                                    >
                                      {item.tourType}
                                    </Text>
                                    <View
                                      style={{
                                        flexDirection: "row",
                                        justifyContent: "space-between",
                                        alignItems: "center",
                                      }}
                                    >
                                      <View
                                        style={{
                                          padding: 8,
                                          marginTop: 4,
                                          borderWidth: 1,
                                          borderColor: "#333",
                                          borderRadius: 14,
                                        }}
                                      >
                                        {item.tourCost.adult == 15000 &&
                                        item.tourCost.adult >= 10000 ? (
                                          <Text
                                            style={{
                                              fontSize: 13,
                                              fontFamily: "Andika",
                                            }}
                                          >
                                             - High
                                          </Text>
                                        ) : item.tourCost.adult < 10000 &&
                                          item.tourCost.adult >= 5000 ? (
                                          <Text
                                            style={{
                                              fontSize: 13,
                                              fontFamily: "NewYorkl",
                                            }}
                                          >
                                             - Medium
                                          </Text>
                                        ) : item.tourCost.adult > 2500 &&
                                          item.tourCost.adult < 500205 ? (
                                          <Text
                                            style={{
                                              fontSize: 13,
                                              fontFamily: "NewYorkl",
                                            }}
                                          >
                                             - Low
                                          </Text>
                                        ) : (
                                          <Text
                                            style={{
                                              fontSize: 13,
                                              fontFamily: "NewYorkl",
                                            }}
                                          >
                                             - Very Low
                                          </Text>
                                        )}
                                      </View>

                                      <View
                                        style={{
                                          flexDirection: "row",
                                          justifyContent: "space-between",
                                          alignItems: "center",
                                        }}
                                      >
                                        <Image
                                          style={{
                                            height: 25,
                                            width: 25,
                                            marginRight: 4,
                                          }}
                                          source={require("../../../assets/Star.png")}
                                        />
                                        <Text
                                          style={{
                                            fontSize: 18,
                                            fontFamily: "NewYorkl",
                                          }}
                                        >
                                          {item.ratings}/5
                                        </Text>
                                      </View>
                                    </View>
                                  </View>
                                </View>
                              </View>
                            </>
                          );
                        }}
                      />
                    )}
                  </>
                )}

                {/* {!loader && tour.length > 0 ? (
                  <TouchableOpacity
                    onPress={() => {
                      // setPage(page + 1);
                      setpageSize(pageSize + 15);
                      getTour();
                    }}
                  >
                    <View
                      style={{ margin: 10, width: WIDTH, alignItems: "center" }}
                    >
                      {tourLoaded ? (
                        <TouchableOpacity
                          onPress={() => {
                            settourLoaded(false);
                          }}
                        >
                          <View
                            style={{
                              // textAlign: "center",
                              backgroundColor: "#FFA26E",
                              paddingHorizontal: 40,
                              paddingVertical: 10,
                              borderRadius: 20,
                            }}
                          >
                            <ActivityIndicator></ActivityIndicator>
                          </View>
                        </TouchableOpacity>
                      ) : (
                        <Text
                          style={{
                            textAlign: "center",
                            backgroundColor: "#FFA26E",
                            paddingHorizontal: 20,
                            paddingVertical: 10,
                            borderRadius: 20,
                          }}
                        >
                          Load More
                        </Text>
                      )}
                    </View>
                  </TouchableOpacity>
                ) : null} */}
              </View>
            )}
          </View>
        );
      case 1:
        return (
          <View style={{ flex: 1, backgroundColor: "white" }}>
            <View
              style={{
                backgroundColor: "white",
              }}
            >
              <StatusBar barStyle="light-content" backgroundColor="#67e6dc" />
              <View
                style={{
                  width: WIDTH,
                  alignItems: "center",
                  justifyContent: "center",
                  height: HEIGHT / 10,
                  backgroundColor: "#67e6dc",
                }}
              >
                <Text style={{ fontSize: 20 }}>Filter Tours</Text>
              </View>
              <ScrollView
                horizontal
                showsHorizontalScrollIndicator={false}
                style={{ flexDirection: "row", marginTop: 50 }}
              >
                {idealFor.map((m, index) => {
                  return (
                    <View key={index}>
                      {selected.includes(m.name) ? (
                        <TouchableOpacity
                          onPress={() => {
                            let city = selected.filter((c) => {
                              return c !== m.name;
                            });
                            setSelected(city);
                          }}
                        >
                          <LinearGradient
                            colors={["#5ED2A0", "#339CB1"]}
                            style={{
                              margin: 10,
                              borderRadius: 25,
                              padding: 15,
                            }}
                          >
                            <Text
                              style={{
                                //   padding: 5,
                                fontFamily: "Avenir",
                                borderColor: "#95afc0",
                              }}
                            >
                              {m.name}
                            </Text>
                          </LinearGradient>
                        </TouchableOpacity>
                      ) : (
                        <TouchableOpacity
                          onPress={() => {
                            setSelected([...selected, m.name]);
                            setIdealType(m.name);
                          }}
                          key={index}
                        >
                          <Text
                            style={{
                              //   padding: 5,
                              fontFamily: "Avenir",
                              borderColor: "#95afc0",
                              borderRadius: 25,
                              padding: 15,
                              borderWidth: 1,
                              margin: 10,
                            }}
                          >
                            {m.name}
                          </Text>
                        </TouchableOpacity>
                      )}
                    </View>
                  );
                })}
              </ScrollView>
              <ScrollView
                horizontal
                showsHorizontalScrollIndicator={false}
                style={{ flexDirection: "row", marginTop: 30 }}
              >
                {tourType.map((m, index) => {
                  return (
                    <View key={index}>
                      {selected.includes(m.name) ? (
                        <TouchableOpacity
                          onPress={() => {
                            let city = selected.filter((c) => {
                              return c !== m.name;
                            });
                            setSelected(city);
                          }}
                        >
                          <LinearGradient
                            colors={["#fcb045", "#339CB1"]}
                            style={{
                              margin: 10,
                              borderRadius: 25,
                              padding: 15,
                            }}
                          >
                            <Text
                              style={{
                                //   padding: 5,
                                fontFamily: "Avenir",
                              }}
                            >
                              {m.name}
                            </Text>
                          </LinearGradient>
                        </TouchableOpacity>
                      ) : (
                        <TouchableOpacity
                          onPress={() => {
                            setSelected([...selected, m.name]);
                            setTourType(m.name);
                          }}
                          key={index}
                        >
                          <Text
                            style={{
                              //   padding: 5,
                              fontFamily: "Avenir",
                              borderColor: "#95afc0",
                              borderRadius: 25,
                              padding: 15,
                              borderWidth: 1,
                              margin: 10,
                            }}
                          >
                            {m.name}
                          </Text>
                        </TouchableOpacity>
                      )}
                    </View>
                  );
                })}
              </ScrollView>
              <ScrollView
                horizontal
                showsHorizontalScrollIndicator={false}
                style={{ flexDirection: "row", marginTop: 30 }}
              >
                {tourCategory.map((m, index) => {
                  return (
                    <View key={index}>
                      {selected.includes(m.name) ? (
                        <TouchableOpacity
                          onPress={() => {
                            let city = selected.filter((c) => {
                              return c !== m.name;
                            });
                            setSelected(city);
                          }}
                        >
                          <LinearGradient
                            colors={["#00d2ff", "#928dab"]}
                            style={{
                              //   borderColor: "#718093",
                              //   borderWidth: 1,
                              margin: 10,
                              borderRadius: 25,
                              padding: 15,
                              opacity: 0.8,
                            }}
                          >
                            <Text
                              style={{
                                fontFamily: "Avenir",
                              }}
                            >
                              {m.name}
                            </Text>
                          </LinearGradient>
                        </TouchableOpacity>
                      ) : (
                        <TouchableOpacity
                          onPress={() => {
                            setSelected([...selected, m.name]);
                            setTourCategory(m.name);
                          }}
                          key={index}
                        >
                          <Text
                            style={{
                              //   padding: 5,
                              fontFamily: "Avenir",
                              borderColor: "#95afc0",
                              borderRadius: 25,
                              padding: 15,
                              borderWidth: 1,
                              margin: 10,
                            }}
                          >
                            {m.name}
                          </Text>
                        </TouchableOpacity>
                      )}
                    </View>
                  );
                })}
              </ScrollView>

              <View
                style={{
                  flexDirection: "row",
                  justifyContent: "space-around",
                  marginHorizontal: 20,
                  marginVertical: 30,
                  alignItems: "center",
                }}
              >
                <TouchableOpacity onPress={() => cancel()}>
                  <Surface style={{ borderRadius: 50, elevation: 10 }}>
                    <Feather name="x" style={{ fontSize: 35, padding: 10 }} />
                  </Surface>
                </TouchableOpacity>
                <TouchableOpacity onPress={() => searchTour()}>
                  <Surface style={{ borderRadius: 50, elevation: 10 }}>
                    <Text
                      style={{
                        fontSize: 23,
                        paddingHorizontal: 13,
                        paddingVertical: 10,
                      }}
                    >
                      
                    </Text>
                  </Surface>
                </TouchableOpacity>
              </View>
            </View>
          </View>
        );
    }
  };
  return <>{render(step)}</>;
};

