import React, { useState, useEffect, useContext } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  ScrollView,
  FlatList,
  Platform,
  Dimensions,
  StatusBar,
  TextInput,
  TouchableOpacity,
  Button,
  ActivityIndicator,
} from "react-native";
import { AuthContext } from "../../context/AuthContext";
import firebase from "firebase";
import touron from "../../api/touron";
import { LinearGradient } from "expo-linear-gradient";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

import { AntDesign, Feather, MaterialIcons } from "@expo/vector-icons";

import Checked from "../CategoryScreens/Reusable components/Checked";

const TourHomeScreen = ({ navigation, route }) => {
  const { isLoggedIn, user, tours } = useContext(AuthContext);
  const [tour, setTour] = useState(tours);
  const [selectedCategory, setSelectedCategory] = useState([]);
  const [selectedTourType, setSelectedTourType] = useState([]);
  const [selectedIdealFor, setSelectedIdealFor] = useState([]);
  const [step, setStep] = useState(0);
  const [filterStep, setFilterStep] = useState(1);

  const nextStep = () => {
    setIdealType("");
    setTourType("");
    setTourCategory("");
    setStep(step + 1);
  };
  const cancel = () => {
    setIdealType("");
    setTourType("");
    setTourCategory("");
    setStep(step - 1);
  };
  const prevStep = () => {
    setLoader(true);

    setStep(step - 1);
    setTimeout(() => {
      setLoader(false);
    }, 2000);
  };

  const getSavedTours = () => {
    let sT = [];
    let sTNames = [];
    if (user) {
      firebase
        .database()
        .ref(`saved-tours/${user.uid}`)
        .on("value", (data) => {
          if (data) {
            data.forEach((c) => {
              sT.push(c.val());
              sTNames.push(c.val().tourName);
            });
          }
        });

      return [sT, sTNames];
    } else {
      return [sT, sTNames];
    }
  };

  const [sT, sTNames] = getSavedTours();
  const [error, setErrorMessage] = useState();
  const [loader, setLoader] = useState(true);
  const [mainLoaded, setMainLoaded] = useState(true);
  const [savedTours, setSavedTours] = useState(sTNames);
  const [savedToursDetails, setSavedToursDetails] = useState(sT);
  const [tourName, setTourName] = useState("");

  const [idealType, setIdealType] = useState("");
  const [tourType, setTourType] = useState("");
  const [tourCategory, setTourCategory] = useState("");
  const [page, setPage] = useState(1);
  const [pageSize, setpageSize] = useState(10);

  let indexValue = 20;
  const showLoader = () => {
    setLoader(false);
  };

  const search = () => {
    if (tour.length > 0) {
      const d = tour.filter((c) => {
        return c.tourName
          .trim()
          .toUpperCase()
          .includes(tourName.trim().toUpperCase().trim());
      });
      return d;
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      if (savedToursDetails.length > 0) {
        firebase
          .database()
          .ref(`saved-tours/${user.uid}`)
          .set(savedToursDetails)
          .then((data) => console.log(data))
          .catch((err) => console.log(err));
      }
    }
  }, [tour, savedToursDetails, indexValue]);

  const getTour = async () => {
    if (route.params.name) {
      const cityname = route.params.name;
      try {
        const tourResponse = await touron.get(`/tour/cityname/${cityname}`);
        if (tourResponse.data.length === 0) {
          setTour([]);
        }
        setTour(tourResponse.data);
      } catch (err) {
        setErrorMessage("Something went wrong");
      }
    }
  };

  const getIdealTypeTours = async () => {
    if (idealType != "") {
      const tourResponse = await touron.get(`/tour/idealtype/${idealType}`);
      // console.log(tourResponse.data);
      setTour(tourResponse.data);
      setStep(step - 1);
    }
  };
  const getTourTypeTours = async () => {
    if (tourType != "") {
      const tourResponse = await touron.get(`/tour/tourtype/${tourType}`);
      setTour(tourResponse.data);
      // console.log(tourResponse.data);
      setStep(step - 1);
    }
  };
  const getTourCategoryTours = async () => {
    if (tourCategory != "") {
      const tourResponse = await touron.get(
        `/tour/tourcategory/${tourCategory}`
      );
      // console.log(tourResponse.data);

      setTour(tourResponse.data);
      setStep(step - 1);
    }
  };

  // const searchTour = async () => {
  //   setLoader(true);
  //   setStep(step - 1);

  //   if (idealType != "") {
  //     const tourResponse = await touron.get(`/tour/idealtype/${idealType}`);
  //     setTour(tourResponse.data);
  //     setLoader(false);
  //   }

  //   if (tourType != "") {
  //     const tourResponse = await touron.get(`/tour/tourtype/${tourType}`);
  //     setTour(tourResponse.data);
  //     setLoader(false);
  //   }
  //   if (tourCategory != "") {
  //     const tourResponse = await touron.get(
  //       `/tour/tourcategory/${tourCategory}`
  //     );
  //     setTour(tourResponse.data);
  //     setLoader(false);
  //   }
  // };

  const renderFilterItem = () => {
    const idealFor = [
      {
        name: "Family",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
      {
        name: "Family and kids",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
      {
        name: "Solo",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
      {
        name: "Mature Couple",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
      {
        name: "Young Couple",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
    ];

    const tourType = [
      {
        name: "Full Day Tour",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
      {
        name: "Half Day Tour",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
      {
        name: "Night Tour",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
    ];
    const tourCategory = [
      {
        name: "Activities",
        imageUrl:
          "https://image.freepik.com/free-vector/local-tourism-concept_23-2148606915.jpg",
      },
      {
        name: "Hop On & Off",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
      {
        name: "Attraction",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
      {
        name: "Learning",
        imageUrl:
          "https://image.freepik.com/free-vector/thank-you-with-character-vector_2029-149.jpg",
      },
    ];
    switch (filterStep) {
      case 0: {
        return (
          <View
            style={{
              alignItems: "center",
              marginVertical: 20,
              height: HEIGHT / 1.8,
              width: WIDTH,
            }}
          >
            <FlatList
              numColumns={2}
              keyExtractor={(item) => item.name}
              data={tourCategory}
              renderItem={({ item }) => {
                return (
                  <TouchableOpacity
                    onPress={() => {
                      if (selectedCategory.includes(item.name)) {
                        let filter = selectedCategory.filter((i) => {
                          return i !== item.name;
                        });
                        setSelectedCategory(filter);
                      } else {
                        setSelectedCategory([...selectedCategory, item.name]);
                      }

                      // setSelectedIdealFor([]);
                    }}
                  >
                    <View
                      style={{
                        margin: 10,
                        alignItems: "center",
                        justifyContent: "center",
                        backgroundColor: "#30336b",
                        height: WIDTH / 2.6,
                        borderRadius: 20,
                        width: WIDTH / 2.6,
                      }}
                    >
                      {selectedCategory.includes(item.name) ? (
                        <View
                          style={{
                            position: "absolute",
                            zIndex: 2,
                            right: 10,
                            top: 10,
                          }}
                        >
                          {/* <Checked /> */}
                          <Image
                            style={{ height: 20, width: 20 }}
                            source={{
                              uri:
                                "https://spng.pngfind.com/pngs/s/216-2167982_check-mark-white-tick-icon-no-background-hd.png",
                            }}
                          />
                        </View>
                      ) : null}
                      <Image
                        style={{
                          height: WIDTH / 8,
                          width: WIDTH / 8,
                          marginBottom: 10,
                        }}
                        source={{ uri: item.imageUrl }}
                      />
                      <Text style={{ color: "#fff", fontFamily: "Andika" }}>
                        {item.name}
                      </Text>
                    </View>
                  </TouchableOpacity>
                );
              }}
            />
          </View>
        );
      }
      case 1: {
        return (
          <View
            style={{
              alignItems: "center",
              marginVertical: 20,
              height: HEIGHT / 1.5,
              width: WIDTH,
            }}
          >
            <FlatList
              numColumns={2}
              keyExtractor={(item) => item.name}
              data={idealFor}
              renderItem={({ item }) => {
                return (
                  <TouchableOpacity
                    onPress={() => {
                      if (selectedIdealFor.includes(item.name)) {
                        let filter = selectedIdealFor.filter((i) => {
                          return i !== item.name;
                        });
                        setSelectedIdealFor(filter);
                      } else {
                        setSelectedIdealFor([...selectedIdealFor, item.name]);
                      }

                      // setSelectedIdealFor([]);
                    }}
                  >
                    <View
                      style={{
                        margin: 10,
                        alignItems: "center",
                        justifyContent: "center",
                        backgroundColor: "#30336b",
                        height: WIDTH / 2.6,
                        borderRadius: 20,
                        width: WIDTH / 2.6,
                      }}
                    >
                      {selectedIdealFor.includes(item.name) ? (
                        <View
                          style={{
                            position: "absolute",
                            zIndex: 2,
                            right: 10,
                            top: 10,
                          }}
                        >
                          {/* <Checked /> */}
                          <Image
                            style={{ height: 20, width: 20 }}
                            source={{
                              uri:
                                "https://spng.pngfind.com/pngs/s/216-2167982_check-mark-white-tick-icon-no-background-hd.png",
                            }}
                          />
                        </View>
                      ) : null}
                      <Image
                        style={{
                          height: WIDTH / 5,
                          width: WIDTH / 5,
                          marginBottom: 10,
                        }}
                        source={{ uri: item.imageUrl }}
                      />
                      <Text style={{ color: "#fff", fontFamily: "Andika" }}>
                        {item.name}
                      </Text>
                    </View>
                  </TouchableOpacity>
                );
              }}
            />
          </View>
        );
      }
      case 2: {
        return (
          <View
            style={{
              alignItems: "center",
              marginVertical: 20,
              height: HEIGHT / 1.8,
              width: WIDTH,
            }}
          >
            <FlatList
              numColumns={2}
              data={tourType}
              renderItem={({ item }) => {
                return (
                  <TouchableOpacity
                    onPress={() => {
                      if (selectedTourType.includes(item.name)) {
                        let filter = selectedTourType.filter((i) => {
                          return i !== item.name;
                        });
                        setSelectedTourType(filter);
                      } else {
                        setSelectedTourType([...selectedTourType, item.name]);
                      }

                      // setSelectedIdealFor([]);
                    }}
                  >
                    <View
                      style={{
                        margin: 10,
                        alignItems: "center",
                        justifyContent: "center",
                        backgroundColor: "#30336b",
                        height: WIDTH / 2.6,
                        borderRadius: 20,
                        width: WIDTH / 2.6,
                      }}
                    >
                      {selectedTourType.includes(item.name) ? (
                        <View
                          style={{
                            position: "absolute",
                            zIndex: 2,
                            right: 10,
                            top: 10,
                          }}
                        >
                          {/* <Checked /> */}
                          <Image
                            style={{ height: 20, width: 20 }}
                            source={{
                              uri:
                                "https://spng.pngfind.com/pngs/s/216-2167982_check-mark-white-tick-icon-no-background-hd.png",
                            }}
                          />
                        </View>
                      ) : null}
                      <Image
                        style={{
                          height: WIDTH / 5,
                          width: WIDTH / 5,
                          marginBottom: 10,
                        }}
                        source={{ uri: item.imageUrl }}
                      />
                      <Text style={{ color: "#fff", fontFamily: "Andika" }}>
                        {item.name}
                      </Text>
                    </View>
                  </TouchableOpacity>
                );
              }}
            />
          </View>
        );
      }
    }
  };

  useEffect(() => {
    showLoader();
    let mounted = true;
    if (mounted) {
      getTour();
    }
    return () => (mounted = false);
  }, [idealType, tourCategory, tourType]);

  const render = (step) => {
    switch (step) {
      case 0:
        return (
          <View
            style={
              ([styles.container],
              {
                backgroundColor: tour.length === 0 ? "white" : "transparent",
                flex: 1,
              })
            }
          >
            {loader ? (
              <ActivityIndicator
                size="large"
                style={{
                  flex: 1,
                  alignItems: "center",
                  justifyContent: "center",
                }}
              />
            ) : (
              <View>
                <View
                  style={{
                    height: HEIGHT / 8,
                    // backgroundColor: "#ff6b6b",
                    padding: 10,
                    marginTop: Platform.OS === "ios" ? HEIGHT / 12 : 40,
                    width: WIDTH,
                    // alignItems: "center",
                    justifyContent: "center",
                  }}
                >
                  <View
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      justifyContent: "space-between",
                      alignItems: "center",
                    }}
                  >
                    <TouchableOpacity onPress={() => navigation.goBack()}>
                      <Feather name="arrow-left" style={{ fontSize: 30 }} />
                    </TouchableOpacity>

                    <View>
                      <Text style={{ fontSize: 23, fontFamily: "NewYorkl" }}>
                        Tours
                      </Text>
                    </View>
                    <TouchableOpacity onPress={() => nextStep()}>
                      <MaterialIcons
                        name="sort"
                        size={24}
                        color="black"
                        style={{ paddingRight: 10 }}
                      />
                    </TouchableOpacity>
                  </View>
                  <View style={styles.background}>
                    <Feather name="search" style={styles.iconStyle}></Feather>
                    <TextInput
                      style={styles.inputStyle}
                      placeholder="Ex.Universal studious singapore"
                      onChangeText={(value) => setTourName(value)}
                      onSubmitEditing={search}
                      autoCapitalize="none"
                      autoCorrect={false}
                      keyboardType="email-address"
                    />
                  </View>
                </View>

                {tour.length === 0 && route.params.name ? (
                  <View
                    style={{ alignItems: "center", justifyContent: "center" }}
                  >
                    <View>
                      <Image
                        style={{ width: WIDTH * 0.9, height: WIDTH * 0.9 }}
                        source={require("../../../assets/oops.jpg")}
                      />
                    </View>
                    <Text style={{ fontFamily: "Avenir", fontSize: 23 }}>
                      No Tours Found for {route.params.name}
                    </Text>
                  </View>
                ) : (
                  <>
                    <FlatList
                      showsVerticalScrollIndicator={false}
                      keyExtractor={(c) => c._id}
                      data={search()}
                      renderItem={({ item, index }) => {
                        return (
                          <>
                            <View style={styles.imageContainer}>
                              <View style={styles.shadow}>
                                <TouchableOpacity
                                  onPress={() => {
                                    navigation.navigate("TourInner", {
                                      item: item,
                                    });
                                  }}
                                >
                                  <Image
                                    style={styles.image}
                                    source={{ uri: item.imageUrl }}
                                  />
                                </TouchableOpacity>
                                <View
                                  style={{
                                    flexDirection: "row",
                                    position: "absolute",
                                    width: WIDTH * 0.9,
                                    justifyContent: "space-between",
                                    alignItems: "center",
                                  }}
                                >
                                  <LinearGradient
                                    colors={["#FFA26E", "#E36D5D"]}
                                    style={{
                                      marginHorizontals: 15,
                                      marginTop: 10,
                                      padding: 2,
                                      borderRadius: 5,
                                      left: 10,
                                    }}
                                  >
                                    <View>
                                      <Text style={styles.cityname}>
                                        {item.cityName}
                                      </Text>
                                    </View>
                                  </LinearGradient>
                                </View>
                                <View style={styles.tourDetails}>
                                  <View
                                    style={{
                                      flexDirection: "row",
                                      justifyContent: "space-between",
                                    }}
                                  >
                                    <Text
                                      style={{
                                        color: "#8395A7",
                                        fontSize: 15,
                                        fontFamily: "Andika",
                                      }}
                                    >
                                      {item.tourCategory.join(",")}
                                    </Text>
                                    <View>
                                      {savedTours.includes(item.tourName) ? (
                                        <TouchableOpacity
                                          onPress={() => {
                                            let filterData = savedTours.filter(
                                              (c) => {
                                                return c != item.tourName;
                                              }
                                            );

                                            setSavedTours(filterData);

                                            const filterDetails = savedToursDetails.filter(
                                              (c) => {
                                                return (
                                                  c.tourName !== item.tourName
                                                );
                                              }
                                            );

                                            setSavedToursDetails(filterDetails);
                                          }}
                                        >
                                          <AntDesign
                                            style={{ marginRight: 0 }}
                                            name="heart"
                                            size={24}
                                            color="#FF4500"
                                          />
                                        </TouchableOpacity>
                                      ) : (
                                        <TouchableOpacity
                                          onPress={() => {
                                            if (isLoggedIn) {
                                              setSavedTours([
                                                ...savedTours,
                                                item.tourName,
                                              ]);
                                              setSavedToursDetails([
                                                ...savedToursDetails,
                                                item,
                                              ]);
                                            } else {
                                              navigation.navigate(
                                                "SignUpScreen"
                                              );
                                            }
                                          }}
                                        >
                                          <Image
                                            style={{
                                              height: 35,

                                              width: 35,
                                            }}
                                            source={require("../../../assets/heart.png")}
                                          />
                                        </TouchableOpacity>
                                      )}
                                    </View>
                                  </View>

                                  <Text
                                    style={{
                                      fontSize: 18,
                                      // fontWeight: "bold",
                                      fontFamily: "NewYorkl",
                                      marginTop: 2,
                                    }}
                                  >
                                    {item.tourName}
                                  </Text>
                                  <Text
                                    style={{
                                      color: "#8395A7",
                                      fontSize: 15,
                                      marginVertical: 5,
                                      fontFamily: "Andika",
                                    }}
                                  >
                                    {item.tourType}
                                  </Text>
                                  <View
                                    style={{
                                      flexDirection: "row",
                                      justifyContent: "space-between",
                                      alignItems: "center",
                                    }}
                                  >
                                    <View
                                      style={{
                                        padding: 8,
                                        marginTop: 4,
                                        borderWidth: 1,
                                        borderColor: "#333",
                                        borderRadius: 14,
                                      }}
                                    >
                                      {item.tourCost.adult == 15000 &&
                                      item.tourCost.adult >= 10000 ? (
                                        <Text
                                          style={{
                                            fontSize: 13,
                                            fontFamily: "Andika",
                                          }}
                                        >
                                          ₹₹₹₹ - High
                                        </Text>
                                      ) : item.tourCost.adult < 10000 &&
                                        item.tourCost.adult >= 5000 ? (
                                        <Text
                                          style={{
                                            fontSize: 13,
                                            fontFamily: "NewYorkl",
                                          }}
                                        >
                                          ₹₹₹ - Medium
                                        </Text>
                                      ) : item.tourCost.adult > 2500 &&
                                        item.tourCost.adult < 500205 ? (
                                        <Text
                                          style={{
                                            fontSize: 13,
                                            fontFamily: "NewYorkl",
                                          }}
                                        >
                                          ₹₹ - Low
                                        </Text>
                                      ) : (
                                        <Text
                                          style={{
                                            fontSize: 13,
                                            fontFamily: "NewYorkl",
                                          }}
                                        >
                                          ₹ - Very Low
                                        </Text>
                                      )}
                                    </View>

                                    <View
                                      style={{
                                        flexDirection: "row",
                                        justifyContent: "space-between",
                                        alignItems: "center",
                                      }}
                                    >
                                      <Image
                                        style={{
                                          height: 25,
                                          width: 25,
                                          marginRight: 4,
                                        }}
                                        source={require("../../../assets/Star.png")}
                                      />
                                      <Text
                                        style={{
                                          fontSize: 18,
                                          fontFamily: "NewYorkl",
                                        }}
                                      >
                                        {item.ratings}/5
                                      </Text>
                                    </View>
                                  </View>
                                </View>
                              </View>
                            </View>
                          </>
                        );
                      }}
                    />
                  </>
                )}
              </View>
            )}
          </View>
        );
      case 1:
        return (
          <ScrollView
            style={{
              backgroundColor: "#130f40",
              flex: 1,
            }}
          >
            <View>
              <View style={{ margin: 10 }}>
                <View
                  style={{
                    marginVertical: 10,
                    flexDirection: "row",
                    alignItems: "center",
                  }}
                >
                  <TouchableOpacity onPress={() => setStep(0)}>
                    <Feather
                      name="arrow-left"
                      size={28}
                      color="#fff"
                      style={{
                        paddingRight: 20,
                      }}
                    />
                  </TouchableOpacity>
                  <Text
                    style={{
                      fontFamily: "NewYorkl",
                      fontSize: 20,
                      color: "#fff",
                    }}
                  >
                    Filter By Category
                  </Text>
                </View>
              </View>
              <View
                style={{
                  padding: 10,
                  borderRadius: 20,
                  alignItems: "center",
                }}
              >
                <View
                  style={{
                    height: HEIGHT / 12,
                    flexDirection: "row",
                    alignItems: "center",
                    borderRadius: 15,
                  }}
                >
                  <TouchableOpacity onPress={() => setFilterStep(0)}>
                    <View
                      style={{
                        height: HEIGHT / 12.6,
                        alignItems: "center",
                        borderTopLefttRadius: 15,
                        borderBottomLeftRadius: 25,
                        justifyContent: "center",
                        paddingHorizontal: 15,
                        backgroundColor: filterStep == 0 ? "#ffeaa7" : "#fff",
                      }}
                    >
                      <Text
                        style={{ textAlign: "center", fontFamily: "Avenir" }}
                      >
                        Tour Category
                      </Text>
                    </View>
                  </TouchableOpacity>
                  <TouchableOpacity onPress={() => setFilterStep(1)}>
                    <View
                      style={{
                        height: HEIGHT / 12.6,
                        alignItems: "center",

                        justifyContent: "center",
                        paddingHorizontal: 15,
                        backgroundColor: filterStep == 1 ? "#ffeaa7" : "#fff",
                      }}
                    >
                      <Text
                        style={{ textAlign: "center", fontFamily: "Avenir" }}
                      >
                        Ideal For
                      </Text>
                    </View>
                  </TouchableOpacity>
                  <TouchableOpacity onPress={() => setFilterStep(2)}>
                    <View
                      style={{
                        height: HEIGHT / 12.6,
                        alignItems: "center",
                        borderTopRightRadius: 25,
                        borderBottomRighRadius: 15,
                        justifyContent: "center",
                        paddingHorizontal: 15,
                        backgroundColor: filterStep == 2 ? "#ffeaa7" : "#fff",
                      }}
                    >
                      <Text
                        style={{ textAlign: "center", fontFamily: "Avenir" }}
                      >
                        Tour Type
                      </Text>
                    </View>
                  </TouchableOpacity>
                </View>
              </View>
              {renderFilterItem()}
            </View>

            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-evenly",
                marginBottom: 20,
              }}
            >
              <TouchableOpacity
                onPress={() => {
                  setSelectedIdealFor([]);
                  setSelectedTourType([]);
                  setSelectedCategory([]);
                }}
              >
                <View>
                  <Text
                    style={{
                      borderRadius: 10,
                      borderWidth: 1,
                      padding: 20,
                      fontSize: 16,
                      color: "#333",
                      backgroundColor: "#dff9fb",
                      fontFamily: "Avenir",
                    }}
                  >
                    Clear Filters
                  </Text>
                </View>
              </TouchableOpacity>
              <TouchableOpacity>
                <View style={{}}>
                  <Text
                    style={{
                      borderRadius: 10,
                      borderWidth: 1,
                      padding: 20,
                      fontSize: 16,
                      color: "#fff",
                      backgroundColor: "#ff7979",
                      fontFamily: "Avenir",
                    }}
                  >
                    Apply Filters
                  </Text>
                </View>
              </TouchableOpacity>
            </View>
          </ScrollView>
        );
    }
  };
  return <>{render(step)}</>;
};

export default TourHomeScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    // paddingTop: 20,
  },
  imageContainer: {
    padding: 5,
    position: "relative",
    paddingTop: 20,
  },
  image: {
    height: Dimensions.get("window").height / 4.5,
    width: WIDTH * 0.9,
    borderRadius: 20,
  },
  shadow: {
    // height: Dimensions.get("window").height / 2.4,
    width: WIDTH * 0.9,
    backgroundColor: "#fff",
    borderRadius: 18,
    marginHorizontal: 10,
  },
  cityname: {
    padding: 5,
    fontSize: 12,
    color: "#fff",
    fontWeight: "bold",
    fontStyle: "normal",

    zIndex: 1,
    left: 0,
  },

  view: {
    width: 100,
    height: 100,
    marginTop: 20,
    backgroundColor: "red",
  },
  tourDetails: {
    margin: 15,
  },
  background: {
    backgroundColor: "#fff",
    height: HEIGHT / 15,
    borderRadius: 20,
    flexDirection: "row",

    //  borderWidth: 1,
    width: WIDTH * 0.9,
    marginVertical: 20,
    // marginHorizontal: 10,
  },
  inputStyle: {
    fontSize: 18,
    flex: 1,
  },
  iconStyle: {
    fontSize: 28,
    alignSelf: "center",
    marginHorizontal: 15,
  },
});
