import React, { useState, useEffect, useContext } from "react";

import {
  StyleSheet,
  Text,
  View,
  Image,
  FlatList,
  Dimensions,
  TouchableOpacity,
  ActivityIndicator,
} from "react-native";

import { Feather } from "@expo/vector-icons";
import touron from "../../api/touron";
import { LinearGradient } from "expo-linear-gradient";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

import SearchBar from "../../Reusable Components/SearchBar";
import { AuthContext } from "../../context/AuthContext";

const SelfTourHome = ({ navigation, route }) => {
  // console.log(route.params.cities,'klklklklkl',route.params.selectedCity)
  const { tours } = useContext(AuthContext);

  const [tour, setTour] = useState([]);
  const [error, setErrorMessage] = useState();
  const [loader, setLoader] = useState(true);
  const selectedCity = route.params.selectedCity;
  const cityDetails = route.params.cityDetails;
  const [active, setActive] = useState(0);
  const cityLength = selectedCity.length - 1;
  const [selectedTours, setSelectedTours] = useState([]);
  const [selectedTourNames, setSelectedTourNames] = useState([]);

  const showLoader = () => {
    setTimeout(() => {
      setLoader(false);
    }, 1000);
  };
  const getTour = async (city) => {
    try {
      console.log(city, "cotty");
      const tourResponse = await touron.get(`/tour/cityname/${city}`);
      console.log(tourResponse.data.length, "length");
      setTour(tourResponse.data);
      setLoader(false);
    } catch (err) {
      console.log(err, "err");
      setErrorMessage("Something went wrong");
    }
  };

  useEffect(() => {
    getTour(selectedCity[active]);
    showLoader();
  }, []);

  return (
    <View style={styles.container}>
      <View style={(styles.view, { shadowOpacity: 1 })}></View>
      {/* {loader ? (
        <ActivityIndicator
          size="large"
          style={{ flex: 1, alignItems: "center", justifyContent: "center" }}
        />
      ) : ( */}
      <View style={{ marginTop: 20 }}>
        <View style={{ marginTop: 0 }}>
          <Text
            style={{
              fontSize: 20,
              textAlign: "center",
              fontFamily: "Avenir",
            }}
          >
            Select Your Tours for {selectedCity[active]}
          </Text>
        </View>
        <SearchBar />

        {loader ? (
          <ActivityIndicator
            size="large"
            style={{
              flex: 1,
              alignItems: "center",
              justifyContent: "center",
            }}
          />
        ) : (
          <FlatList
            showsVerticalScrollIndicator={false}
            keyExtractor={(c) => c._id}
            data={tour}
            renderItem={({ item }) => {
              return (
                <View style={{ position: "relative" }}>
                  {selectedTourNames.includes(item.tourName) ? (
                    <TouchableOpacity
                      style={styles.tickImageContainer}
                      onPress={() => {
                        let tours = selectedTourNames.filter((c) => {
                          return c !== item.tourName;
                        });
                        setSelectedTourNames(tours);

                        let updatedTours = selectedTours.filter((c) => {
                          return c.tourName !== item.tourName;
                        });
                        setSelectedTours(updatedTours);
                      }}
                    >
                      <Image
                        style={styles.tickImage}
                        source={require("../../../assets/tick.png")}
                      />
                    </TouchableOpacity>
                  ) : null}

                  <View style={styles.imageContainer}>
                    <View style={styles.shadow}>
                      <TouchableOpacity
                        onPress={() => {
                          setSelectedTourNames([
                            ...selectedTourNames,
                            item.tourName,
                          ]);
                          //  console.log(item);
                          setSelectedTours([
                            ...selectedTours,
                            item,
                            // {
                            //   cityName: item.cityName,
                            //   tourName: item.tourName,
                            //   duration: item.tourDuration,
                            //   tourType: item.tourType,
                            //   idealType: item.idealType,
                            //   pickUpType: item.pickUpPoint,
                            //   imageUrl: item.imageUrl,
                            //   isFinal: true,
                            // },
                          ]);

                          //    navigation.navigate("SelfTourInner", { item: item });
                        }}
                      >
                        <Image
                          style={styles.image}
                          source={{ uri: item.imageUrl }}
                        />
                      </TouchableOpacity>
                      <View style={styles.cityNameContainer}>
                        <LinearGradient
                          colors={["#FFA26E", "#E36D5D"]}
                          style={styles.gradient}
                        >
                          <View>
                            <Text style={styles.cityname}>{item.cityName}</Text>
                          </View>
                        </LinearGradient>
                        <View>
                          <Feather
                            name="bookmark"
                            color="#fff"
                            style={styles.iconStyle}
                          ></Feather>
                        </View>
                      </View>
                      <View style={styles.tourDetails}>
                        <Text style={styles.tourFeatures}>
                          {item.tourCategory.join(", ")}
                        </Text>
                        <Text
                          style={{
                            fontSize: 18,
                            fontFamily: "Avenir",
                          }}
                        >
                          {item.tourName}
                        </Text>
                        <Text style={styles.tourFeatures}>{item.tourType}</Text>
                        <View style={styles.star}>
                          <View style={styles.costContainer}>
                            {item.tourCost.adult == 15000 &&
                            item.tourCost.adult >= 10000 ? (
                              <Text style={styles.cost}>₹₹₹₹ - High</Text>
                            ) : item.tourCost.adult < 10000 &&
                              item.tourCost.adult >= 5000 ? (
                              <Text style={styles.cost}>₹₹₹ - Medium</Text>
                            ) : item.tourCost.adult > 2500 &&
                              item.tourCost.adult < 500205 ? (
                              <Text style={styles.cost}>₹₹ - Low</Text>
                            ) : (
                              <Text style={styles.cost}>₹ - Very Low</Text>
                            )}
                          </View>
                          <View
                            style={[
                              styles.costContainer,
                              {
                                paddingVertical: 3,
                                paddingHorizontal: 5,
                              },
                            ]}
                          >
                            <TouchableOpacity
                              onPress={() => {
                                navigation.navigate("SelfTourInner", {
                                  item: item,
                                });
                              }}
                            >
                              <Text
                                style={{ fontSize: 13, fontFamily: "Andika" }}
                              >
                                See More
                              </Text>
                            </TouchableOpacity>
                          </View>

                          <View style={styles.star}>
                            <Image
                              style={{
                                height: 25,
                                width: 25,
                                marginRight: 4,
                              }}
                              source={require("../../../assets/Star.png")}
                            />
                            <Text style={{ fontSize: 18 }}>
                              {item.ratings}/5
                            </Text>
                          </View>
                        </View>
                      </View>
                    </View>
                  </View>
                </View>
              );
            }}
          />
        )}

        <View style={styles.buttonContainer}>
          {active == 0 ? null : (
            <TouchableOpacity
              style={{ flex: 0.5 }}
              onPress={() => {
                const cityLength = selectedCity.length - 1;

                //  console.log(active);
                if (active <= cityLength) {
                  getTour(selectedCity[active - 1]);
                  setActive(active - 1);
                }
                setLoader(true);
              }}
            >
              <View style={[styles.button, { marginRight: 1 }]}>
                <Text style={styles.buttonText}>Back</Text>
              </View>
            </TouchableOpacity>
          )}
          {cityLength == active ? null : (
            <TouchableOpacity
              style={{ flex: 1.5 }}
              onPress={() => {
                const cityLength = selectedCity.length - 1;

                //   console.log(active);
                if (active <= cityLength) {
                  setActive(active + 1);
                  getTour(selectedCity[active + 1]);
                }
                setLoader(true);
              }}
            >
              <View style={styles.button}>
                <Text style={styles.buttonText}>
                  Proceed to {selectedCity[active + 1]}
                </Text>
              </View>
            </TouchableOpacity>
          )}
          {cityLength == active ? (
            <TouchableOpacity
              style={{ flex: 1.5 }}
              onPress={() => {
                navigation.navigate("OverviewTours", {
                  selectedTours: selectedTours,
                  cityDetails: cityDetails,
                });
              }}
            >
              <View style={styles.button}>
                <Text style={styles.buttonText}>Next</Text>
              </View>
            </TouchableOpacity>
          ) : null}
        </View>
      </View>
      {/* )} */}
    </View>
  );
};

