import React, { useEffect, useState, useContext } from "react";
import {
  StyleSheet,
  Text,
  View,
  Dimensions,
  ActivityIndicator,
  Picker,
  Image,
  Platform,
  ScrollView,
  TouchableOpacity,
  FlatList,
} from "react-native";
import { AuthContext } from "../../context/AuthContext";
import { DataTable } from "react-native-paper";
import { Avatar } from "react-native-paper";
import { Feather, FontAwesome } from "@expo/vector-icons";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;
import * as firebase from "firebase";

const MyRequestScreen = ({ navigation }) => {
  const [loaded, setLoaded] = useState(true);
  const [surpriseTour, setSurpriseTour] = useState([]);
  const [plannedTour, setPlannedTour] = useState([]);
  const [roadTrip, setRoadTrip] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const { user } = useContext(AuthContext);
  const fullData = [...surpriseTour, ...plannedTour, ...roadTrip];
  const [allRequest, setAllRequest] = useState([]);
  const [status, setStatus] = useState("");
  const [category, setCategory] = useState("");
  const itemsPerPage = 5;
  const [page, setPage] = useState(0);
  const from = page * itemsPerPage;
  const to = (page + 1) * itemsPerPage;
  console.log(user, "admin");
  const getUserData = () => {
    if (user !== null) {
      firebase
        .database()
        .ref(`userGeneralInfo/${user.uid}`)
        .on("value", (data) => {
          if (data.val() !== null) {
            let val = data.val();
            console.log(val, "val");
            setIsAdmin(val.admin);
          }
        });
    }
  };

  const getAllRequests = (value) => {
    let request = [];
    firebase
      .database()
      .ref(`requests`)
      .on("value", (data) => {
        // console.log(data.val(), "data");
        if (data) {
          data.forEach((c) => {
            request.push(c.val());
          });
        }
      });

    setAllRequest(request);
    console.log(request, "d");
  };

  const filterDataByType = () => {
    if (status !== "") {
      const filterStatus = allRequest.filter((r) => {
        console.log(r.status, status, "r");
        return r.status
          .trim()
          .toUpperCase()
          .includes(status.trim().toUpperCase());
      });
      console.log(filterStatus, "ststis");
      return filterStatus;
    }

    if (category !== "") {
      const tourCategory = allRequest.filter((r) => {
        // console.log(r.status, status, "r");
        return r.tourCategory
          .trim()
          .toUpperCase()
          .includes(category.trim().toUpperCase());
      });
      console.log(tourCategory, "cte");

      return tourCategory;
    }

    return allRequest;
  };
  useEffect(() => {
    getUserData();
    setCategory("");
    setStatus("");
  }, []);
  useEffect(() => {
    getAllRequests();
    getUserRequests();
  }, []);

  const getUserRequests = () => {
    firebase
      .database()
      .ref(`requests`)
      .on("value", (data) => {
        if (data) {
          let pT = [];
          data.forEach((c) => {
            if (
              c.val().userID === user.uid &&
              c.val().tourCategory === "Planned Tour"
            ) {
              pT.push(c.val());
            }
          });
          setPlannedTour(pT);
        }
      });

    firebase
      .database()
      .ref(`requests`)
      .on("value", (data) => {
        if (data) {
          let rT = [];
          data.forEach((c) => {
            if (
              c.val().userID === user.uid &&
              c.val().tourCategory === "Road Trip"
            ) {
              rT.push(c.val());
            }
          });
          setRoadTrip(rT);
        }
      });
    firebase
      .database()
      .ref(`requests`)
      .on("value", (data) => {
        if (data) {
          let sT = [];
          data.forEach((c) => {
            if (
              c.val().userID === user.uid &&
              c.val().tourCategory === "Surprise Tour"
            ) {
              sT.push(c.val());
            }
          });
          setSurpriseTour(sT);
        }
      });
  };

  useEffect(() => {
    setTimeout(() => {
      setLoaded(false);
    }, 1500);
  });

  // useEffect(() => {}, []);

  const colors = [
    {
      name: "Query Received",
      color: "#f39c12",
    },
    {
      name: "Plan Shared",
      color: "#7f8c8d",
    },
    {
      name: "On Progress",
      color: "#8e44ad",
    },
    {
      name: "Cancelled",
      color: "red",
    },
    {
      name: "On Hold",
      color: "#3498db",
    },
    {
      name: "Duplicate Query",
      color: "#fbc531",
    },
    {
      name: "Tour Booked",
      color: "#2d3436",
    },
    {
      name: "Awaiting Payment",
      color: "#00cec9",
    },
    {
      name: "Cancellation Requested",
      color: "#d63031",
    },
    {
      name: "Estimated",
      color: "#2d3436",
    },
    {
      name: "Completed",
      color: "#55efc4",
    },
  ];

  const getColor = (status) => {
    let color = "";
    colors.filter((c) => {
      if (c.name === status) color = c.color;
    });
    console.log(color, "ss");
    return color;
  };

  console.log(getColor(), "color");

  return (
    <ScrollView style={{ flex: 1, backgroundColor: "#FFF" }}>
      {loaded ? (
        <View
          style={{ flex: 1, alignItems: "center", justifyContent: "center" }}
        >
          <ActivityIndicator size="large" color="black" />
        </View>
      ) : (
        <View>
          {isAdmin ? (
            <View>
              <TouchableOpacity onPress={() => navigation.toggleDrawer()}>
                <View>
                  <Feather
                    name="arrow-left"
                    size={28}
                    color="black"
                    style={{
                      paddingHorizontal: 20,
                      paddingTop: Platform.OS === "ios" ? HEIGHT / 12 : 20,
                    }}
                  />
                </View>
              </TouchableOpacity>
              <View
                style={{
                  alignItems: "center",
                  marginHorizontal: 30,
                  marginVertical: 30,
                }}
              >
                <Image
                  style={{
                    height: HEIGHT / 5,
                    width: HEIGHT / 5,
                    marginTop: 10,
                  }}
                  source={require("../../../assets/playstore.png")}
                />

                <Text
                  style={{ marginTop: 20, fontSize: 16, fontFamily: "Avenir" }}
                >
                  Admin : {user.email}
                </Text>
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center",
                    marginTop: 20,
                  }}
                >
                  <Text
                    style={{
                      fontSize: 16,
                      fontFamily: "Avenir",
                    }}
                  >
                    Status :
                  </Text>
                  <FontAwesome
                    name="circle"
                    size={20}
                    color="#7bed9f"
                    style={{
                      marginLeft: WIDTH / 10,
                    }}
                  />
                  <Text
                    style={{
                      fontSize: 16,
                      fontFamily: "Avenir",
                      marginHorizontal: 10,
                    }}
                  >
                    Online
                  </Text>
                </View>
              </View>
              <View
                style={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "center",
                  alignItems: "center",
                  marginVertical: 20,
                }}
              >
                <Text style={{ fontSize: 20, marginHorizontal: 20 }}>
                  Filter :
                </Text>

                <Picker
                  selectedValue={category}
                  style={{ height: 50, width: WIDTH / 3 }}
                  onValueChange={(itemValue, itemIndex) => {
                    console.log(itemValue, "ko");
                    setCategory(itemValue);
                    setStatus("");
                    filterDataByType();
                  }}
                >
                  <Picker.Item label="All" value="" />
                  <Picker.Item label="Planned Tour" value="Planned Tour" />
                  <Picker.Item label="Road Trip" value="Road Trip" />
                  <Picker.Item label="Surprise Tour" value="Surprise Tour" />
                </Picker>
                <Picker
                  selectedValue={status}
                  style={{ height: 50, width: WIDTH / 3 }}
                  onValueChange={(itemValue, itemIndex) => {
                    console.log(itemValue, "ko");
                    setStatus(itemValue);
                    setCategory("");
                    filterDataByType();
                  }}
                >
                  <Picker.Item label="All" value="" />
                  <Picker.Item
                    label="Duplicate Query"
                    value="Duplicate Query"
                  />
                  <Picker.Item label="Estimated" value="Estimated" />
                  <Picker.Item label="Query Received" value="Query Received" />
                  <Picker.Item label="On Hold" value="On Hold" />
                  <Picker.Item label="On Progress" value="On Progress" />
                  <Picker.Item label="Plan Shared" value="Plan Shared" />
                  <Picker.Item
                    label="Awaiting Payment"
                    value="Awaiting Payment"
                  />
                  <Picker.Item label="Tour Booked" value="Tour Booked" />
                  <Picker.Item label="Completed" value="Completed" />
                  <Picker.Item label="Cancelled" value="Cancelled" />
                  <Picker.Item
                    label="Cancellation Requested"
                    value="Cancellation Requested"
                  />
                </Picker>
              </View>
              <DataTable>
                <DataTable.Header>
                  <DataTable.Title>Request Id</DataTable.Title>
                  <DataTable.Title numeric>Tour Category</DataTable.Title>
                  <DataTable.Title numeric>Request Status</DataTable.Title>
                  {/* <DataTable.Title numeric>Destination</DataTable.Title> */}
                </DataTable.Header>

                <FlatList
                  data={filterDataByType()}
                  keyExtractor={(item) => item.requestID}
                  renderItem={({ item }) => {
                    return (
                      <TouchableOpacity
                        onPress={() => {
                          navigation.navigate("RequestInner", {
                            planned:
                              item.tourCategory === "Planned Tour"
                                ? item
                                : null,
                            road:
                              item.tourCategory === "Road Trip" ? item : null,
                            surprise:
                              item.tourCategory === "Surprise Tour"
                                ? item
                                : null,
                          });
                        }}
                      >
                        <DataTable.Row>
                          <DataTable.Cell>{item.requestID}</DataTable.Cell>
                          <DataTable.Cell numeric>
                            {item.tourCategory}
                          </DataTable.Cell>
                          <DataTable.Cell numeric style={{ padding: 10 }}>
                            <Text
                              style={{
                                backgroundColor: `${getColor(item.status)}`,
                                margin: 5,
                                borderRadius: 50,
                                fontSize: 15,
                                fontFamily: "Andika",
                                padding: 10,
                                color: "white",
                              }}
                            >
                              {item.status}
                            </Text>
                          </DataTable.Cell>
                          {/* <DataTable.Cell numeric>{D.destination}</DataTable.Cell> */}
                        </DataTable.Row>
                      </TouchableOpacity>
                    );
                  }}
                />

                <DataTable.Pagination
                  page={page}
                  numberOfPages={Math.floor(allRequest.length / itemsPerPage)}
                  onPageChange={(page) => setPage(page)}
                  label={`${from + 1}-${to} of ${allRequest.length}`}
                />
              </DataTable>
            </View>
          ) : (
            <View>
              <View
                style={{
                  backgroundColor: "#28C9E1",
                  height: HEIGHT / 8,
                  alignItems: "center",
                  //  justifyContent: "center",
                  flexDirection: "row",
                }}
              >
                <TouchableOpacity onPress={() => navigation.toggleDrawer()}>
                  <View style={{ flex: 0.2, height: 30 }}>
                    <Feather
                      name="arrow-left"
                      size={28}
                      color="black"
                      style={{
                        paddingHorizontal: 20,
                        paddingTop: Platform.OS === "ios" ? 0 : 0,
                      }}
                    />
                  </View>
                </TouchableOpacity>
                <View
                  style={{
                    flex: 0.8,
                    alignItems: "center",
                    justifyContent: "center",
                    paddingTop: 15,
                  }}
                >
                  <Text style={{ color: "white", fontSize: 20 }}>
                    My Requests
                  </Text>
                </View>
              </View>

              {fullData.length == 0 ? (
                <View
                  style={{
                    alignItems: "center",
                    justifyContent: "center",
                    flex: 1,
                  }}
                >
                  <Image
                    style={{
                      height: HEIGHT / 2,
                      width: WIDTH * 0.7,
                      alignItems: "center",
                      justifyContent: "center",
                    }}
                    source={require("../../../assets/myrequest.png")}
                  />
                  <Text
                    style={{
                      fontFamily: "Avenir",
                      fontSize: 20,
                      marginTop: -WIDTH / 10,
                    }}
                  >
                    No Requests Yet
                  </Text>
                  <Text
                    style={{
                      marginTop: 20,
                      fontFamily: "WSansl",
                      fontSize: 18,
                      textAlign: "center",
                    }}
                  >
                    Go to Home and start planning
                  </Text>
                </View>
              ) : (
                <View>
                  {plannedTour.length == 0 ? null : (
                    <View>
                      {/* <Text>Planned Tour</Text> */}
                      <FlatList
                        data={plannedTour}
                        keyExtractor={(item) => item.requestID}
                        renderItem={({ item }) => {
                          return (
                            <View>
                              <TouchableOpacity
                                onPress={() =>
                                  navigation.navigate("RequestInner", {
                                    planned: item,
                                    road: null,
                                    surprise: null,
                                  })
                                }
                              >
                                <View
                                  style={{
                                    flexDirection: "row",
                                    marginTop: 20,
                                    justifyContent: "space-between",
                                  }}
                                >
                                  <View style={{ flex: 0.3 }}>
                                    <Avatar.Text
                                      // label={user.displayName ? user.displayName.charAt(0) : "N"}
                                      label="P"
                                      style={{
                                        backgroundColor: "#DBE8EB",
                                        marginLeft: 20,
                                      }}
                                    />
                                  </View>
                                  <View
                                    style={{
                                      flex: 0.7,
                                      justifyContent: "center",
                                    }}
                                  >
                                    <Text style={{ fontSize: 20 }}>
                                      {item.tourCategory}
                                    </Text>
                                    <Text style={{ fontSize: 14 }}>
                                      Status: {item.status}
                                    </Text>
                                  </View>
                                </View>
                              </TouchableOpacity>
                            </View>
                          );
                        }}
                      />
                    </View>
                  )}
                  {roadTrip.length == 0 ? null : (
                    <View>
                      <FlatList
                        data={roadTrip}
                        keyExtractor={(item) => item.requestID}
                        renderItem={({ item }) => {
                          return (
                            <View>
                              <TouchableOpacity
                                onPress={() =>
                                  navigation.navigate("RequestInner", {
                                    road: item,
                                    planned: null,
                                    surprise: null,
                                  })
                                }
                              >
                                <View
                                  style={{
                                    flexDirection: "row",
                                    marginTop: 20,
                                    justifyContent: "space-between",
                                  }}
                                >
                                  <View style={{ flex: 0.3 }}>
                                    <Avatar.Text
                                      // label={user.displayName ? user.displayName.charAt(0) : "N"}
                                      label="R"
                                      style={{
                                        backgroundColor: "#DBE8EB",
                                        marginLeft: 20,
                                      }}
                                    />
                                  </View>
                                  <View
                                    style={{
                                      flex: 0.7,
                                      justifyContent: "center",
                                    }}
                                  >
                                    <Text style={{ fontSize: 20 }}>
                                      {item.tourCategory}
                                    </Text>
                                    <Text style={{ fontSize: 14 }}>
                                      Status: {item.status}
                                    </Text>
                                  </View>
                                </View>
                              </TouchableOpacity>
                            </View>
                          );
                        }}
                      />
                    </View>
                  )}
                  {surpriseTour.length == 0 ? null : (
                    <View>
                      <FlatList
                        data={surpriseTour}
                        keyExtractor={(item) => item.requestID}
                        renderItem={({ item }) => {
                          //  console.log(item, "ITEM");
                          return (
                            <View>
                              <TouchableOpacity
                                onPress={() =>
                                  navigation.navigate("RequestInner", {
                                    surprise: item,
                                    road: null,
                                    planned: null,
                                  })
                                }
                              >
                                <View
                                  style={{
                                    flexDirection: "row",
                                    marginTop: 20,
                                    justifyContent: "space-between",
                                  }}
                                >
                                  <View style={{ flex: 0.3 }}>
                                    <Avatar.Text
                                      // label={user.displayName ? user.displayName.charAt(0) : "N"}
                                      label="S"
                                      style={{
                                        backgroundColor: "#DBE8EB",
                                        marginLeft: 20,
                                      }}
                                    />
                                  </View>
                                  <View
                                    style={{
                                      flex: 0.7,
                                      justifyContent: "center",
                                    }}
                                  >
                                    <Text style={{ fontSize: 20 }}>
                                      {item.tourCategory}
                                    </Text>
                                    <Text style={{ fontSize: 14 }}>
                                      Status: {item.status}
                                    </Text>
                                  </View>
                                </View>
                              </TouchableOpacity>
                            </View>
                          );
                        }}
                      />
                    </View>
                  )}
                </View>
              )}
            </View>
          )}
        </View>
      )}
    </ScrollView>
  );
};

export default MyRequestScreen;

const styles = new StyleSheet.create({
  modalView: {
    margin: 20,

    backgroundColor: "#fff",
    borderRadius: 20,
    //  padding: 40,
    //  alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 15,
  },
});
