import React, { useContext, useState, useEffect } from "react";
import {
  View,
  StyleSheet,
  Text,
  Image,
  ImageBackground,
  Dimensions,
  Platform,
} from "react-native";
import * as firebase from "firebase";
import AsyncStorage from "@react-native-community/async-storage";
import { DrawerItem, DrawerContentScrollView } from "@react-navigation/drawer";
import { FontAwesome5, Fontisto, Feather, AntDesign } from "@expo/vector-icons";
import { Drawer } from "react-native-paper";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

import { AuthContext } from "../context/AuthContext";
import { TouchableOpacity } from "react-native-gesture-handler";

const DrawerContent = (props) => {
  const [isAdmin, setIsAdmin] = useState(false);

  const { user, isLoggedIn, setIsLoggedIn, setUser } = useContext(AuthContext);
  useEffect(() => {
    getUserData();
  });

  // console.log(isAdmin, "isAdmin");
  // console.log(isLoggedIn, "in");

  const getUserData = () => {
    if (user !== null) {
      firebase
        .database()
        .ref(`userGeneralInfo/${user.uid}`)
        .on("value", (data) => {
          if (data.val() !== null) {
            let val = data.val();
            // console.log(val, "val");
            setIsAdmin(val.admin);
          }
        });
    }
  };
  // console.log(user, "USER");

  const removeToken = async () => {
    try {
      await AsyncStorage.removeItem("userToken");
    } catch (e) {
      console.log(err);
    }
    // console.log("Done.");
  };

  return (
    <View style={{ flex: 1 }}>
      <ImageBackground
        style={{
          width: WIDTH * 0.75,
          height: HEIGHT + 30,
          zIndex: -1,
          opacity: Platform.OS === "ios" ? 0.4 : 1,
        }}
        source={{
          uri:
            "https://images.pexels.com/photos/2108813/pexels-photo-2108813.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
        }}
      />

      {isLoggedIn ? (
        <View
          style={{
            backgroundColor: Platform.OS === "ios" ? "transparent" : "#000000",
            width: WIDTH * 0.75,
            height: HEIGHT + 30,
            position: "absolute",
          }}
        >
          <DrawerContentScrollView {...props}>
            <View style={{ marginVertical: 40 }}>
              {isAdmin ? (
                <>
                  <DrawerItem
                    label={() => <Text style={styles.label}>Home</Text>}
                    style={{ color: "white" }}
