import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  StyleSheet,
  StatusBar,
} from "react-native";
import { LinearGradient } from "expo-linear-gradient";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;
import * as firebase from "firebase";

const PromotionPage = (props) => {
  console.log(props, "props");
  const [users, setUsers] = useState([]);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  console.log(users, "usedscwdvr");
  const getExpoId = () => {
    let user = [];
    firebase
      .database()
      .ref("/userGeneralInfo")
      .on("value", (data) => {
        data.forEach((c) => {
          // console.log(c.val());
          user.push(c.val().pushNotificationToken);
        });
      });

    setUsers(user);
  };

  const send = () => {
    // console.log(u, "ui");
    users.forEach((u) => {
      console.log(u, "io");
      sendPushNotification(u);
    });
  };

  const sendPushNotification = async (expoToken) => {
    console.log(expoToken, "messag");
    const messages = {
      to: expoToken,
      sound: "default",
      title: title,
      body: body,
      // data: { data: "goes here" },
    };

    await fetch("https://exp.host/--/api/v2/push/send", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Accept-encoding": "gzip, deflate",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(messages),
    });
  };

  useEffect(() => {
    getExpoId();
  }, []);
  return (
    <View
      style={{
        flex: 1,
        // justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#81ecec",
      }}
    >
      <View style={{ marginTop: HEIGHT / 10 }}>
        <Text style={{ fontSize: 30, fontFamily: "Andika" }}>
          Promotional page
        </Text>
      </View>

      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Password"
          keyboardType="visible-password"
          keyboardAppearance="dark"
          keyboardType="email-address"
          onChangeText={(value) => setPassword(value)}
          passwordRules={true}
          secureTextEntry={true}
        />
      </View>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Password"
          keyboardType="visible-password"
          keyboardAppearance="dark"
          keyboardType="email-address"
          onChangeText={(value) => setPassword(value)}
          passwordRules={true}
          secureTextEntry={true}
        />
      </View>
    </View>
  );
};

export default PromotionPage;

const styles = new StyleSheet.create({
  input: {
    marginHorizontal: 20,
    width: WIDTH * 0.8,
    height: 60,
    color: "white",
    fontFamily: "Andika",
  },
  inputContainer: {
    height: 60,
    borderRadius: 10,
    backgroundColor: "#0005",
    marginBottom: 20,
  },
});
