import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  Dimensions,
  StyleSheet,
  TextInput,
} from "react-native";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;
import * as firebase from "firebase";
import { Spinner } from "native-base";

const PromotionPage = (props) => {
  // console.log(props, "props");
  const [users, setUsers] = useState([]);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [loaded, setLoaded] = useState(false);
  const [finishMessage, setFinishMessage] = useState(false);

  // console.log(users, "usedscwdvr");
  // console.log(title, body);
  const getExpoId = () => {
    let user = [];
    firebase
      .database()
      .ref("/userGeneralInfo")
      .on("value", (data) => {
        data.forEach((c) => {
          // console.log(c.val());
          user.push(c.val().pushNotificationToken);
        });
      });

    setUsers(user);
  };

  const send = () => {
    setLoaded(true);
    try {
      users.forEach((u) => {
        console.log(u, "iowkjbcb");
        // sendPushNotification(u);
      });
      setLoaded(false);
      setFinishMessage(true);
    } catch (err) {
      console.log(err);
    }
  };

  const sendPushNotification = async (expoToken) => {
    console.log(expoToken, "messag");
    const messages = {
      to: expoToken,
      sound: "default",
      title: title,
      body: body,
      // data: { data: "goes here" },
    };

    await fetch("https://exp.host/--/api/v2/push/send", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Accept-encoding": "gzip, deflate",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(messages),
    });
  };

  useEffect(() => {
    getExpoId();
  }, []);
  return (
    <View
      style={{
        flex: 1,
        // justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#81ecec",
        shadowColor: "red",
        shadowOpacity: 1,
      }}
    >
      <View style={{ marginTop: HEIGHT / 10 }}>
        <Text style={{ fontSize: 30, fontFamily: "Andika" }}>
          Promotional page
        </Text>
      </View>
      <View style={{ alignItems: "center", justifyContent: "center", flex: 1 }}>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.input}
            placeholder="Title"
            keyboardType="visible-password"
            keyboardAppearance="dark"
            keyboardType="email-address"
            onChangeText={(value) => setTitle(value)}
          />
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.input}
            placeholder="Body"
            keyboardType="visible-password"
            keyboardAppearance="dark"
            keyboardType="email-address"
            onChangeText={(value) => setBody(value)}
          />
        </View>
        <View style={styles.otpButtonContainer}>
          {loaded ? (
            <View style={{ paddingVertical: -10 }}>
              <Spinner color="white" />
            </View>
          ) : (
            <>
              {finishMessage ? (
                <TouchableOpacity
                  onPress={() => {
                    setFinishMessage(false);
                    setTitle("");
                    setBody("");
                  }}
                >
                  <Text style={styles.optButtonText}>
                    {" "}
                    Notification Has been sent to all users
                  </Text>
                </TouchableOpacity>
              ) : (
                <TouchableOpacity
                  onPress={() => {
                    send();
                  }}
                >
                  <Text style={styles.optButtonText}>Send Notification</Text>
                </TouchableOpacity>
              )}
            </>
          )}
        </View>
      </View>
    </View>
  );
};

export default PromotionPage;

const styles = new StyleSheet.create({
  optButtonText: {
    textAlign: "center",
    paddingVertical: 20,
    color: "white",
    fontSize: 16,
    fontFamily: "Andika",
    //  fontWeight: "bold",
  },
  otpButtonContainer: {
    marginBottom: HEIGHT / 8,
    backgroundColor: "black",
    borderRadius: 10,
    width: WIDTH * 0.9,
    alignContent: "center",
    position: "relative",
  },
  input: {
    marginHorizontal: 20,
    width: WIDTH * 0.8,
    height: 60,
    color: "white",
    fontFamily: "Andika",
  },
  inputContainer: {
    height: 60,
    borderRadius: 10,
    backgroundColor: "#0005",
    marginBottom: 20,
  },
});
