import React, { useEffect, useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  Button,
  FlatList,
  Picker,
  Dimensions,
  Image,
  ScrollView,
  ActivityIndicator,
} from "react-native";
import DropDownPicker from "react-native-dropdown-picker";
import touron from "../../api/touron";
import * as firebase from "firebase";
import { Surface } from "react-native-paper";
import ContentList from "../HomeScreens/components/ContentList";
import { TouchableOpacity } from "react-native-gesture-handler";

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

const BlogHomeScreen = ({ navigation }) => {
  const [blog, setBlog] = useState([]);

  const [loaded, setLoaded] = useState(true);
  const [blogloaded, setBlogLoaded] = useState(true);
  const [page, setPage] = useState(1);
  const [pageSize, setpageSize] = useState(10);
  console.log(page, pageSize, "lp");

  const getBlog = async () => {
    setBlogLoaded(true);
    try {
      const blogResponse = await touron.get(
        `/blog?page=1&pageSize=${pageSize}`
      );
      // console.log(blogResponse.data, "lll");
      setBlog(blogResponse.data);
      setBlogLoaded(false);
    } catch (err) {
      console.log(err, "err");
    }
  };

  useEffect(() => {
    getBlog();
    setTimeout(() => {
      setLoaded(false);
    }, 1000);
  }, []);

  return (
    <ScrollView>
      <View style={{ margin: 25 }}>
        <ContentList
          title={"Blogs Name"}
          more={""}
          content={"Content Goes Here"}
        />
      </View>

      {/* <View
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "center",
          alignItems: "center",
          marginVertical: 20,
        }}
      >
        <Text style={{ fontSize: 20, marginHorizontal: 20 }}>Filter :</Text>

        <Picker
          // selectedValue={category}
          style={{ height: 50, width: WIDTH / 3 }}
          onValueChange={(itemValue, itemIndex) => {}}
        >
          <Picker.Item label="Planned Tour" value="Planned Tour" />
          <Picker.Item label="Road Trip" value="Road Trip" />
          <Picker.Item label="Surprise Tour" value="Surprise Tour" />
        </Picker>
      </View> */}
      {loaded ? (
        <View
          style={{ flex: 1, alignItems: "center", justifyContent: "center" }}
        >
          <ActivityIndicator size="large" />
        </View>
      ) : (
        <FlatList
          numColumns={2}
          data={blog}
          keyExtractor={(item) => item._id}
          renderItem={({ item }) => {
            // console.log(item);
            return (
              <View
                style={{
                  width: WIDTH / 2,
                  marginHorizontal: 5,
                  // alignItems: "center",
                  // justifyContent: "center",
                  display: "flex",
                  flexDirection: "row",
                }}
              >
                <TouchableOpacity
                  onPress={() =>
                    navigation.navigate("BlogInner", { item: item })
                  }
                >
                  <Surface
                    style={{
                      width: WIDTH / 2.1,
                      marginHorizontal: 0,
                      marginVertical: 5,
                      borderRadius: 20,
                      elevation: 5,
                      height: HEIGHT / 2.5,
                    }}
                  >
                    <View>
                      <Image
                        style={{
                          height: HEIGHT / 4.8,
                          width: WIDTH / 2.1,
                          borderRadius: 15,
                        }}
                        source={{ uri: item.imageSrc }}
                      />
                    </View>
                    <View>
                      <Text
                        style={{
                          fontSize: 15,
                          fontFamily: "NewYorkl",
                          marginHorizontal: 10,
                          marginTop: 4,
                        }}
                      >
                        {item.blogTitle}
                      </Text>
                    </View>
                    <View style={{ marginHorizontal: 10 }}>
                      <Text style={{ fontSize: 14, fontFamily: "Andika" }}>
                        {item.content.slice(0, 35)}...
                      </Text>
                    </View>
                  </Surface>
                </TouchableOpacity>
              </View>
            );
          }}
        />
      )}

      {loaded ? null : (
        <TouchableOpacity
          onPress={() => {
            // setPage(page + 1);
            setpageSize(pageSize + 10);
            getBlog();
          }}
        >
          <View style={{ margin: 10, width: WIDTH, alignItems: "center" }}>
            {blogloaded ? (
              <View
                style={{
                  // textAlign: "center",
                  backgroundColor: "#95a5a6",
                  paddingHorizontal: 40,
                  paddingVertical: 10,
                  borderRadius: 20,
                }}
              >
                <ActivityIndicator></ActivityIndicator>
              </View>
            ) : (
              <Text
                style={{
                  textAlign: "center",
                  backgroundColor: "#95a5a6",
                  paddingHorizontal: 20,
                  paddingVertical: 10,
                  borderRadius: 20,
                }}
              >
                Load More ...
              </Text>
            )}
          </View>
        </TouchableOpacity>
      )}
    </ScrollView>
  );
};

export default BlogHomeScreen;
