import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  Text,
  View,
  StatusBar,
  Image,
  TouchableOpacity,
  FlatList,
  Dimensions,
  ScrollView,
} from "react-native";
import { AntDesign } from "@expo/vector-icons";

import touron from "../../api/touron";

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;
import useData from "../../hooks/useData";
import { Surface } from "react-native-paper";
const BlogInnerScreen = ({ navigation, route }) => {
  const item = route.params.item;
  const [click, setClick] = useState(false);
  const [country, city, tour, errorMessage] = useData();

  const filteredTour = (name) => {
    return tour.filter((c) => {
      return c.countryName.includes(name);
    });
  };

  console.log(filteredTour(), "cjnjhjlg");

  useEffect(() => {
    let mounted = true;
    if (mounted) {
      filteredTour();
    }
    return () => (mounted = false);
  }, []);

  //console.log(item, "ITEM");
  return (
    <ScrollView style={{ flex: 1, backgroundColor: "#FFF" }}>
      <StatusBar />
      <View
        style={{
          //backgroundColor: "#333",
          height: HEIGHT / 13,
          alignItems: "center",
          justifyContent: "space-around",
          flexDirection: "row",
        }}
      >
        <TouchableOpacity onPress={() => navigation.goBack()}>
          <View
            style={{
              alignItems: "center",
              justifyContent: "center",
              paddingTop: 7,
              //  paddingRight: HEIGHT / 10,
              flex: 0.2,
            }}
          >
            <AntDesign name="arrowleft" size={24} color="black" />
          </View>
        </TouchableOpacity>
        <View
          style={{
            justifyContent: "center",
            paddingTop: 10,
            flex: 0.6,
            //  paddingLeft: WIDTH / 4,
            // marginRight: HEIGHT / 8,
          }}
        >
          <Text style={{ color: "black", fontSize: 30, fontFamily: "WSans" }}>
            {item.countryName}
          </Text>
        </View>
      </View>
      <View>
        <Image
          style={{ width: WIDTH, height: HEIGHT / 3 }}
          source={{ uri: item.imageSrc }}
        />
      </View>

      <View
        style={{ flexDirection: "row", alignItems: "center", paddingLeft: 10 }}
      >
        <Text
          style={{
            backgroundColor: "transparent",
            borderColor: "black",
            padding: 8,
            margin: 8,
            fontSize: 12,
            fontFamily: "WSansl",
            borderWidth: 1,
            borderRadius: 10,
          }}
        >
          {item.countryName}
        </Text>
        {item.cityName.length == 0 ? null : (
          <Text
            style={{
              backgroundColor: "transparent",
              borderColor: "black",
              padding: 8,
              margin: 8,
              fontSize: 12,
              fontFamily: "WSansl",
              borderWidth: 1,
              borderRadius: 10,
            }}
          >
            {item.cityName[0]}
          </Text>
        )}

        {!click ? (
          <TouchableOpacity onPress={() => setClick(true)}>
            {item.cityName.length > 2 ? (
              <Text>+ {item.cityName.length - 1}</Text>
            ) : null}
          </TouchableOpacity>
        ) : (
          <>
            {item.cityName.map((c, index) => {
              if (index > 0) {
                return (
                  <Text
                    style={{
                      backgroundColor: "transparent",
                      position: "relative",
                      borderColor: "black",
                      padding: 8,
                      margin: 5,
                      fontSize: 12,
                      fontFamily: "WSansl",
                      borderWidth: 1,
                      zIndex: 10,
                      borderRadius: 10,
                    }}
                  >
                    {c}
                  </Text>
                );
              }
            })}
            <TouchableOpacity onPress={() => setClick(false)}>
              <View style={{ bottom: 10, zIndex: 20 }}>
                <Text
                  style={{
                    padding: 3,
                    fontSize: 20,
                    fontFamily: "Avenir",
                    //  zIndex: 100,
                  }}
                >
                  X
                </Text>
              </View>
            </TouchableOpacity>
          </>
        )}
      </View>

      <Text style={{ fontFamily: "NewYorkl", fontSize: 30, margin: 10 }}>
        {item.blogTitle}
      </Text>
      <View style={{ flexDirection: "row", alignItems: "center" }}>
        <View
          style={{
            marginHorizontal: 10,
          }}
        >
          <Image
            style={{ width: WIDTH / 8, height: WIDTH / 8, borderRadius: 50 }}
            source={require("../../../assets/logo.jpeg")}
          />
        </View>
        <Text style={{ fontFamily: "Andika", fontSize: 16 }}>
          PUBLISHED {item.createdAt.slice(0, 10)} {"\n"}by tour On Team
        </Text>
      </View>

      <Text
        style={{
          marginVertical: 10,
          marginHorizontal: 15,
          fontSize: 15,
          lineHeight: HEIGHT / 40,
        }}
      >
        {item.content}
      </Text>

      {item.subHeading1 == "" ? null : (
        <View style={{ margin: 10 }}>
          <Text style={{ fontFamily: "NewYorkl", fontSize: 18 }}>
            {item.subHeading1}
          </Text>
        </View>
      )}
      {item.imageSrc1 == "" ? null : (
        <View>
          <Image
            style={{ width: WIDTH, height: HEIGHT / 3 }}
            source={{ uri: item.imageSrc1 }}
          />
        </View>
      )}
      {item.content1 == "" ? null : (
        <Text
          style={{
            marginVertical: 10,
            marginHorizontal: 15,
            fontSize: 15,
            lineHeight: HEIGHT / 40,
          }}
        >
          {item.content1}
        </Text>
      )}

      {item.subHeading2 == "" ? null : (
        <View style={{ margin: 10 }}>
