import React, { useState, useEffect, useContext } from "react";
import {
  StyleSheet,
  Text,
  View,
  StatusBar,
  FlatList,
  ScrollView,
  Platform,
  Dimensions,
  TouchableOpacity,
  Image,
} from "react-native";
import { Buffer } from "buffer";
import AsyncStorage from "@react-native-community/async-storage";
import useData from "../../hooks/useData";
import * as Network from "expo-network";
import Categories from "./components/CategoriesScreen";
import ContentList from "./components/ContentList";
import { Feather, AntDesign } from "@expo/vector-icons";
import * as Font from "expo-font";

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

import { AuthContext } from "../../context/AuthContext";
import touron from "../../api/touron";
import { AppLoading } from "expo";

const HomeScreen = ({ navigation, route }) => {
  // const [country, city, tour, errorMessage] = useData();
  const { user } = useContext(AuthContext);
  const [status, setStatus] = useState();
  const [fontLoaded, setFont] = useState(false);
  const [loaded, setLoaded] = useState(true);
  const [tour, setTour] = useState([]);
  const [countries, setCountries] = useState([]);
  const [banner, setBanner] = useState([]);

  const [cities, setCities] = useState([]);

  // console.log(countries);

  useEffect(() => {
    getCounries();
    getCities();
    getTours();
  }, []);

  const fetchFont = async () => {
    await Font.loadAsync({
      // Futura: require("../../../assets/fonts/Futura Std Medium.ttf"),
      Andika: require("../../../assets/fonts/Andika-Regular.ttf"),
      Avenir: require("../../../assets/fonts/AvenirLTStd-Black.otf"),
      NewYorkl: require("../../../assets/fonts/NewYorkLargeBlack.otf"),
      WSans: require("../../../assets/fonts/WorkSans-Black.ttf"),
      WSansl: require("../../../assets/fonts/WorkSans-Light.ttf"),
      SFProDisplayRegular: require("../../../assets/fonts/SF-Pro-Display-Regular.otf"),
      SFProTextRegular: require("../../../assets/fonts/SF-Pro-Text-Regular.otf"),
    });

    setFont(true);
  };

  const getTours = async () => {
    const tours = await touron.get("/tour?page=32&pageSize=10");
    // console.log(tours.data, "page");
    setTour(tours.data);
  };
  const getCounries = async () => {
    const country = await touron.get("/country?page=2&pageSize=10");
    // console.log(country.data, "page");
    setCountries(country.data);
  };
  const getCities = async () => {
    const city = await touron.get("/city?page=2&pageSize=10");
    // console.log(city.data, "page");
    setCities(city.data);
  };
  const getNetwork = async () => {
    const status = (await Network.getNetworkStateAsync()).isConnected;
    console.log(status, "STATUS");
    setStatus(status);
  };

  const getImage = async () => {
    const photo = await touron.get("/banner");
    console.log(photo.data[0].photo.data, "page");
    setBanner(photo.data);
  };
  useEffect(() => {
    getImage();
  }, []);
  useEffect(() => {
    let mounted = true;
    if (mounted) {
      getNetwork();
      fetchFont();
      setTimeout(() => {
        setLoaded(false);
      }, 2000);
    }

    return () => (mounted = false);
  }, []);

  const arrayBufferToBase64 = (buffer) => {
    let binary = "";
    let bytes = new Uint8Array(buffer);
    let len = bytes.byteLength;
    for (let i = 0; i < len; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return window.btoa(binary);
  };

  // const filteredCountry = () => {
  //   const date = new Date();
  //   const months = [
  //     "January",
  //     "February",
  //     "March",
  //     "April",
  //     "May",
  //     "June",
  //     "July",
  //     "August",
  //     "September",
  //     "October",
  //     "November",
  //     "December",
  //   ];
  //   console.log(months[date.getMonth()], "date");

  //   const currentMonth = months[date.getMonth() + 1];
  //   if (countries.length > 0)
  //     return countries.filter((c) => {
  //       return c.general.bestTimeToVisit.includes(currentMonth);
  //     });
  // };
  // const filteredCity = () => {
  //   if (cities.length > 0)
  //     return cities.filter((c) => {
  //       return c.travelDuration == "8-10 hours";
  //     });
  // };
  // const filteredTour = () => {
  //   if (tours.length > 0)
  //     return tours.filter((c) => {
  //       return c.idealType.includes("Young Couple");
  //     });
  // };

  if (!fontLoaded) {
    return <AppLoading />;
  }

  return (
    <ScrollView
      style={{ backgroundColor: "#fff" }}
      showsVerticalScrollIndicator={false}
    >
      {/* <StatusBar
        barStyle="dark-content"
        backgroundColor="transparent"
        animated={true}
      /> */}
      {status ? (
        <View style={styles.container}>
          <View>
            <TouchableOpacity>
              <View style={{ height: HEIGHT / 10, width: HEIGHT / 10 }}>
                <Feather
                  name="menu"
                  size={28}
                  color="black"
                  style={{ paddingHorizontal: 10, paddingTop: 20 }}
                  onPress={() => navigation.toggleDrawer()}
                />
              </View>
            </TouchableOpacity>
          </View>

          <View
            style={{
              width: window.width,
              flexDirection: "row",
              marginRight: WIDTH / 20,
            }}
          >
            {user == null ? (
              <Text style={styles.title}>Hey, Start Planning your...</Text>
            ) : (
              <Text style={styles.title}>
                Hey, {user.displayName} Start Planning your...
              </Text>
            )}
            {/* <Text style={styles.title}>
              Hey, {user.displayName} {""}Start Planning...
