          <ContentList
            route={"Promotion"}
            navigation={navigation}
            title={"Hot Deals"}
            more={""}
            content={"Content Goes Here"}
          />
          <FlatList
            data={banner}
            horizontal
            showsHorizontalScrollIndicator={false}
            keyExtractor={(d) => d._id}
            renderItem={({ item, index }) => {
              console.log(item._id, "datataatt");
              return (
                <TouchableOpacity>
                  <View style={styles.tileStyle}>
                    <Text style={([styles.name], { color: "black" })}>
                      sdgberdgbsg
                    </Text>

                    <Image
                      fadeDuration={1000}
                      style={styles.tourImage}
                      source={{
                        uri:
                          "data:image/jpeg;base64," +
                          arrayBufferToBase64(item.photo.data.data), //data.data in your case
                      }}
                    />
                  </View>
                </TouchableOpacity>
              );
            }}
          />
          <ContentList
            route={"TourHome"}
            navigation={navigation}
            title={"Curated Tours"}
            more={"Show More"}
            content={"Content Goes Here"}
          />
          <FlatList
            data={tour}
            horizontal
            showsHorizontalScrollIndicator={false}
            keyExtractor={(d) => d._id}
            renderItem={({ item, index }) => {
              if (index < 4)
                return (
                  <TouchableOpacity
                    onPress={() => {
                      navigation.navigate("TourInner", { item: item });
                    }}
                  >
                    <View style={styles.tileStyle}>
                      <Text style={styles.name}>{item.tourName}</Text>

                      <Image
                        fadeDuration={1000}
                        style={styles.tourImage}
                        source={{ uri: item.imageUrl }}
                      />
                    </View>
                  </TouchableOpacity>
                );
            }}
          />
        </View>
      ) : (
        <>
          <View style={{ width: WIDTH, height: HEIGHT }}>
