import React from "react";
import {
  View,
  Image,
  TouchableOpacity,
  KeyboardAvoidingView,
  TouchableWithoutFeedback,
  Keyboard,
  Text,
  TextInput,
  Dimensions,
} from "react-native";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

const Destination = ({
  imgSrc,
  destination,
  startPoint,
  preferanece,
  setDestination,
  setPreferanece,
  setStartPoint,
}) => {
  return (
    <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>
      <KeyboardAvoidingView style={{ flex: 1 }} behavior="position">
        <View style={{ alignItems: "center" }}>
          <View
            style={{
              height: HEIGHT / 2.5,
              width: WIDTH,
              alignItems: "center",
              marginTop: 30,
            }}
          >
            <Image
              style={{ height: HEIGHT / 3, width: WIDTH * 0.8 }}
              source={{ uri: imgSrc }}
            />
          </View>

          <View>
            <Text
              style={{
                fontSize: 18,
                textAlign: "center",
                fontFamily: "NewYorkl",
              }}
            >
              FIND THE BEST PLANNED TOUR
            </Text>
            <View
              style={{
                height: HEIGHT / 2.65,
                marginTop: 20,
                width: WIDTH * 0.9,
                marginHorizontal: 40,
                // justifyContent: "space-around",
                alignItems: "center",
              }}
            >
              <View
                style={{
                  height: HEIGHT / 13,
                  flexDirection: "row",
                  width: WIDTH * 0.9,
                  justifyContent: "space-around",
                  alignItems: "center",
                }}
              >
                <View style={{ width: WIDTH / 2.5 }}>
                  <Text style={{ fontSize: 14, fontFamily: "Andika" }}>
                    Enter the holiday destination
                  </Text>
                </View>
                <View>
                  <TextInput
                    value={destination}
                    style={{ width: 100 }}
                    multiline
                    onChangeText={(value) => setDestination(value)}
                    placeholder="Ex.Maldives"
                  />
                </View>
              </View>
              <View
                style={{
                  height: HEIGHT / 13,
                  flexDirection: "row",
                  width: WIDTH * 0.9,
                  justifyContent: "space-around",
                  alignItems: "center",
                }}
              >
                <View style={{ width: WIDTH / 2.5 }}>
                  <Text style={{ fontSize: 14, fontFamily: "Andika" }}>
                    Departure City
                  </Text>
                </View>
                <View>
                  <TextInput
                    value={startPoint}
                    multiline
                    style={{ width: 100 }}
                    onChangeText={(value) => setStartPoint(value)}
                    placeholder="Ex.Chennai"
                  />
                </View>
              </View>
              <View
                style={{
                  height: HEIGHT / 13,
                  width: WIDTH * 0.9,
                  justifyContent: "center",
                  alignItems: "center",
                  marginTop: 10,
                }}
              >
                <View>
                  <Text
                    style={{
                      fontSize: 14,
                      marginLeft: 10,
                      fontFamily: "Andika",
                    }}
                  >
                    Your preferences when you travel (Optional):
                  </Text>
                </View>
                <View style={{ alignItems: "center", marginTop: 10 }}>
                  <TextInput
                    style={{
                      borderColor: "#333",
                      width: WIDTH * 0.75,
                    }}
                    value={preferanece}
                    multiline
                    onChangeText={(value) => setPreferanece(value)}
                    placeholder="Favourite food like that ...."
                  />
                </View>
              </View>
            </View>
          </View>
        </View>
      </KeyboardAvoidingView>
    </TouchableWithoutFeedback>
  );
};

export default Destination;
